INSERT INTO Book (book_id, title, publisher, isbn, image, book_info, price, published_date, created_at, modified_at,author) VALUES
    (1, '철 없는 사랑', '인디펍', '9791167565501', 'https://indiepub.kr/web/product/big/202405/036a0fc163bc55ae171c3d69042c21d8.png', '철 없는 사랑\n봄, 여름, 가을, 겨울. 나의 사랑은 철이 없었다.\n\n사철 가리지 않고 재잘재잘 잘도 떠드는 당신\n그런 당신과 함께한 사랑의 흔적\n\n실천하는 사랑이 모여 점점 더 커지는 사랑의 덩어리\n시가 되고 노래가 된 우리의 이야기', 14000, '2024-04-01', NOW(), NOW(),'김민준'),
    (2, '사실은 살고 싶었다', '보노로', '9791198626721', 'https://indiepub.kr/web/product/big/202404/b7fb190efa7f507e5ae36f992c6f02f7.png', '구체적인 우울증 치료 과정을 써내려갔습니다. 특히 치료자들과의 대화 내용을 살렸다는 점이 특징입니다. 우울, 불안 및 자살충동 등 사회에서 잘 드러내지 않는 감정을 솔직하게 표현했습니다. 결론적으로 우리는 혼자가 아니라는 메시지를 통해 위로를 전달했습니다.', 12000, '2024-04-01', NOW(), NOW(),'이지은'),
    (3, '성수기도 없는데 비수기라니', '주식회사 구름산책', '9791191830002', 'https://indiepub.kr/web/product/big/202404/a78620f0dcd60e2c1dfd32b470964858.jpg', '책방은 견디는 게 일인지도 모릅니다.\n경제의 땅에서는 당연히. 그러나 그 비즈니스의 세계를 살짝 빗겨서 보면 낭만이 보이기 시작합니다.\n그렇다고 확신합니다. 그런 의미에서 책방을 오래오래 하겠다는 다짐으로 글을 썼습니다.', 12000, '2024-04-01', NOW(), NOW(),'박지훈');
